note_models.css:
.card {
 font-family: arial;
 font-size: 20px;
 text-align: align;
 color: black;
 background-color: white;
}

/* { "Jura-Vorlagen-Version" : 29 } */



/* Fragenfolge */

.aktuelleFrage a{
 color: #ffc2c6;
}

.nightMode .aktuelleFrage a{
 color: #ffc2c6;
}

:not(.nightMode) .aktuelleFrage a.gesetz {
 color: #ffc2c6;
 animation-name: fadeIn_link_aktuelle_Frage;
 animation-duration: 1s;
}

.nightMode .aktuelleFrage a.gesetz {
 color: #ffc2c6;
 animation-name: fadeIn_link_aktuelle_Frage!important;
 animation-duration: 1s;
}

@keyframes fadeIn_link_aktuelle_Frage {
 from {color: white;}
 to   {color: #ffc2c6;}
}

.alteFrage {
 max-width:600px;
 overflow:auto;
 background-color:#E6E6E6;
 padding:5px;
}

.nightMode .alteFrage {
 background-color: #474747;
}

.aktuelleFrage {
 max-width:600px;
 overflow:auto;
 background-color: #C40101;
 color: white;
 padding: 5px;
}

.nightMode .aktuelleFrage {
 background-color: rgb(132, 1, 1);
}

body {
 margin-bottom: 6em;
}



/*allgemein - Variante 1*/

.card {
 color: black;
 background-color: white;
 overflow:visible;
 padding-bottom: 5px;
 font-family: Arial;
 font-size: 15px; /* Allgemeine Schriftgröße */
 text-align: left;
 line-height: 1.35;
}

.mobile .card {
 font-size: 15px; /* Allgemeine Schriftgröße -- auf dem Smartphone */
 padding: 5px;
 padding-bottom: 50px;
}

.zusatzinfo {
 font-size: 13px; /* Schriftgröße für Elemente wie "Tags", "Aufdecken", "Verbesserungsvorschlag" */
 max-width: 500px;
}

.mobile .zusatzinfo  {
 font-size: 13px; /* Schriftgröße für Elemente wie "Tags", "Aufdecken", "Verbesserungsvorschlag" -- auf dem Smartphone */
}

.user_tabelle, table tr td table {
 font-size: 14px; /* Schriftgröße für Tabellen, die in den Feldern einer Notiz enthalten sind (also solche Tabellen, die ein User der Jura-Vorlagen erstellt hat und die NICHT automatisch als Teil einer Jura-Vorlage generiert werden wie z.B. bei "Streitstand" oder "vergleichende Frage") */
 border: 1px solid grey;
 border-collapse: collapse;
 width: 100%;
}

.mobile .user_tabelle, table tr td table {
 font-size: 14px; /* Schriftgröße für Tabellen, die in den Feldern einer Notiz enthalten sind (also solche Tabellen, die ein User der Jura-Vorlagen erstellt hat und die NICHT automatisch als Teil einer Jura-Vorlage generiert werden wie z.B. bei "Streitstand" oder "vergleichende Frage") -- auf dem Smartphone */
}

.user_tabelle th, table tr td table th {
 text-align: center;
 padding: 3px;
 border: 1px solid grey;
}

.user_tabelle td, table tr td table td {
 padding: 3px;
 border: 1px solid grey;
}

td {
 padding: 0px;
 max-width:600px;
}

a {
 text-decoration: none;
 color: grey; /* Butler-Addon-Code: links_normal_color */
}

.gesetz {
 animation-name: fadeIn;
 animation-duration: 1s;
 color: grey; /* Butler-Addon-Code: ges_normal_color_1 */
}

@keyframes fadeIn {
 from {color: black;}
 to   {color: grey} /* Butler-Addon-Code: ges_normal_color_2 */
}

.nightMode a {
 color: #a4a4a4; /* Butler-Addon-Code: links_nightmode_color */
}

.nightMode a.gesetz {
 animation-name: fadeIn_night_mode;
 animation-duration: 1s;
 color: #a4a4a4; /* Butler-Addon-Code: ges_nightmode_color_1 */
}

@keyframes fadeIn_night_mode {
 from {color: white;}
 to   {color: #a4a4a4;} /* Butler-Addon-Code: ges_nightmode_color_2 */
}

a:hover, a:focus {
 text-decoration: underline;
 color: grey; /* Butler-Addon-Code: links_normal_color */
}

.nightMode a:hover, .nightMode a:focus {
 color: #a4a4a4; /* Butler-Addon-Code: links_nightmode_color */
}

.Druck {
 display: none;
}

.UeberAnzeigen {
    cursor: pointer;
    width:0px;
    height:0px;
    border-top:30px solid transparent;
    border-left: 30px solid transparent;
    border-right: 0px solid transparent;

}.UeberAnzeigen:hover {
    border-top:30px solid #DFDFDF;

}.UeberAnzeigen:active {
    border-top:30px solid grey;
}

.UeberAusblenden {
    cursor: pointer;
    width:0px;
    height:0px;
    border-top:30px solid #DFDFDF;
    border-left: 30px solid transparent;
    border-right: 0px solid transparent;
}

.UeberAusblenden:hover {
    border-top:30px solid #BEBEBE;
}

.UeberAusblenden:active {
    border-top:30px solid grey;
}

.prio1 {
 color: #DF0101;
 float: right;
}

.prio2 {
 color: #0078d2;
 float: right;
}

.prio3 {
 float: right;
}

/* body {
 margin-bottom: 3em;
} */

