        $('#mnemonics').show(0)
        $('#extra').show(0)

        // The setTimeout is needed on AnkiMobile so that the cloze is rendered before we try to toggle it
        setTimeout(function () {
            $('.genuine-cloze').each(function (index, elem) {
                toggleCloze(elem, 'answer')
            })
        }, 0)
    })
</script>

